            //   FuelRod = 13,
            //   BansheeFuelRod = 14,
            //   AntiAir = 15,
            //   Explosive = 16,
            //   TankExplosive = 17,
            //   Artillery = 18,
            //   Missile = 19,
            //   Grenade = 20,
            //   Demolition = 21,
            //   Beam = 22,
            //   WarthogRam = 23,
            //   GaussCannon = 24,
            //   BruteShot = 25,
            //   Melee = 26,
            //   SuicideGrunt = 27,
            //   SuicideGruntAV = 28,
            //   SuperSentinel = 29,
            //   Hero = 30,
            //   CampaignHero = 31,
            //   ScarabBeam = 32,
            //   Unknown = 255
            // Additional damage types may be added in the future.
            "int": {

              // Number of attacks of the given damage type that were successfully landed
              // on the victim building or unit.
              "AttacksLanded": "int"
            }
          }
        }
      }
    }
  },

  // Elapsed time, measured in milliseconds, since the point when the match started.
  "TimeSinceStartMilliseconds": "int",

