  "InstanceId": "int",

  // Location of the objective.
  "Location": {

    "x": "double",

    "y": "double",

    "z": "double"
  },

  // Elapsed time, measured in milliseconds, since the point when the match started.
  "TimeSinceStartMilliseconds": "int",

  // Name of the event.
