var rp = require('request-promise');

var options = {
  method: 'GET',
  body: {
    url: 'https://example.com'
  },
  uri: 'https://api.link.fish/Urls/apps',
  auth: {
    user: '{EMAIL}',
    pass: '{API-TOKEN}'
  },
  json: true
};

rp(options)
  .then(function (data) {
    // Request succeeded
    console.log(JSON.stringify(data,null,2));
  })
  .catch(function (err) {
    // Request failed
    console.log(err.statusCode + ': ' + err.error);
  });

