var request = require('request');
var fs = require('fs');

const options = {
  method: 'GET',
  qs: {
    url: 'https://example.com'
  },
  encoding: 'binary',
  uri: 'https://api.link.fish/Urls/browser-screenshot',
  auth: {
    user: '{EMAIL}',
    pass: '{API-TOKEN}'
  },
};

request.get(options, function(err, response, body) {
  if (!err) {
    fs.writeFile('screenshot.png', body, 'binary', function (err) {});
  }
});

